package br.com.enjoei2.perfil;

import br.com.enjoei2.perfil.dto.ClientReducedDTO;
import br.com.enjoei2.perfil.model.Client;
import br.com.enjoei2.perfil.model.Gender;
import br.com.enjoei2.perfil.service.impl.PerfilServiceImpl;
import org.junit.Assert;
import org.junit.Rule;
import org.junit.Test;
import org.junit.rules.ExpectedException;
import org.junit.runner.RunWith;
import org.mockito.junit.MockitoJUnitRunner;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.junit4.SpringRunner;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import br.com.enjoei2.perfil.dao.ClientRepository;
import br.com.enjoei2.perfil.exceptions.BadRequestException;

import java.sql.Date;
import java.util.Optional;

import static org.mockito.ArgumentMatchers.any;
import static org.mockito.ArgumentMatchers.anyLong;
import static org.mockito.ArgumentMatchers.anyObject;
import static org.mockito.Mockito.when;

@RunWith(MockitoJUnitRunner.class)
public class PerfilApplicationTests {

    @InjectMocks
    PerfilServiceImpl perfilService;

    @Mock
    ClientRepository clientRepository;

    @Rule
    public ExpectedException exception = ExpectedException.none();

    @Test
    public void imageGetsUploadedWhenThereIsClientUpdate(){
        Client clientInfo = new Client();
        clientInfo.setClientId(1L);
        clientInfo.setEmail("teste@teste.com");
        clientInfo.setPassword("1234");
        clientInfo.setFirstName("Penguin");
        clientInfo.setLastName("Emperor");
        clientInfo.setCpf("1234");
        clientInfo.setDdd("1234");
        clientInfo.setPhone("1234");
        clientInfo.setBirthDate(Date.valueOf("1900-05-15"));
        clientInfo.setSex(Gender.MALE);
        clientInfo.setCep("1234");
        Optional<Client> client = Optional.of(clientInfo);

        Client newClient = new Client();

        newClient.setProfileImage("");

        when(clientRepository.findById(anyLong())).thenReturn(client);
        when(clientRepository.save(any())).thenReturn(null);

        try {
            Client updatedClient = perfilService.updateClient(Optional.of(newClient),1L);
            Assert.assertNotNull(updatedClient.getProfileImage());

            when(clientRepository.findById(anyLong())).thenReturn(Optional.of(updatedClient));
            ClientReducedDTO crd = perfilService.retrieveClient(1L);
            Assert.assertNotNull(crd.getProfileImage());
        } catch (Exception e) {
            e.printStackTrace();
        }



    }


}
